import { Component } from '@angular/core';
import { NavigationStart, Router } from '@angular/router';
import { filter } from 'rxjs';

@Component({
  selector: 'app-app-header',
  templateUrl: './app-header.component.html',
  styleUrls: ['./app-header.component.css']
})
export class AppHeaderComponent {
  url: string = '/';
  constructor(
    private route: Router
  ) {
    this.route.events.pipe(
      filter(event => event instanceof NavigationStart)
    ).subscribe((event: any) => {
      this.url = event?.url;
    });
  }


  ngOnInit(): void {

  }
  gotourl(url: string): void {
    this.route.navigate(["/"+url]);
  }

}
<div class="main">
    <div (click)="gotourl('')" [class.selected]="url === '/'">Home</div>
    <div (click)="gotourl('blog-list')" [class.selected]="url === '/blog-list'">Blog List</div>
    <!-- <div (click)="gotourl('about-us')" [class.selected]="url === '/about-us'">AboutUs</div> -->
    <div (click)="gotourl('login')" [class.selected]="url === '/login'">Login</div>
    <!-- <div (click)="gotourl('contact-us')" [class.selected]="url === '/contact-us'">Contactus</div> -->
</div>


    <!-- Navigation Bar -->
    <header>
        <div class="navbar">
            <div class="logo">MyBlog</div>
            <nav>
                <a href="#" (click)="gotourl('')" [class.selected]="url === '/'">Home</a>
                <a href="#">About</a>
                <a href="#">Contact</a>
                <a href="#">Sign In</a>
            </nav>
        </div>
    </header>

    <!-- Main Hero Section -->
    <section class="hero">
        <div class="hero-content">
            <h1>Welcome to My Blog</h1>
            <p>Your source for the latest trends, stories, and more.</p>
            <button class="cta-button">Get Started</button>
        </div>
    </section>

    <!-- Footer -->
    <footer>
        <p>&copy; 2025 MyBlog. All rights reserved.</p>
    </footer>
