<!-- Navigation Bar -->
<header>
    <div class="navbar">
        <div class="logo">MyBlog</div>
        <nav>
            <div href="#" (click)="gotourl('')" [class.selected]="url === '/'">Home</div>
            <div href="#" (click)="gotourl('blog-list')" [class.selected]="url === '/blog-list'">BlogList</div>
            <div href="#" (click)="gotourl('login')" [class.selected]="url === '/login'">Sign In</div>
        </nav>
    </div>
</header>

<!-- Main Hero Section -->
<section class="hero">
    <div class="hero-content">
