import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';

@Component({
  selector: 'app-add-comment',
  templateUrl: './add-comment.component.html',
  styleUrls: ['./add-comment.component.css']
})
export class AddCommentComponent {

  content: string = '';
  constructor(
    //In constructor argument pass component class name i.e OrderHistoryDialogComponent
    public dialogRef: MatDialogRef<AddCommentComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any
  ){}

  closeDialog(): void {
    this.dialogRef.close({content: this.content});
  }

  addComment(): void {
    this.closeDialog();
  }
}
<div class="close" (click)="closeDialog()">
    <mat-icon>close</mat-icon>
</div>
<div class="comment-part">
    Comment:
    <textarea [(ngModel)]="content" rows="10" cols="90"></textarea>
</div>
<div>
    <button mat-raised-button  color="primary" (click)="addComment()">Submit</button>
</div>
