import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { take } from 'rxjs';
import { BlogService } from 'src/app/components/service/blog-service.service';
import { Blog } from 'src/app/model/blog.model';

@Component({
  selector: 'app-blog-list',
  templateUrl: './blog-list.component.html',
  styleUrls: ['./blog-list.component.css']
})
export class BlogListComponent {

  blogList: Array<Blog> = [];

  constructor(
    private blogService: BlogService,
    private route: Router
  ) {
   this.getAllBlogList();
  }

  getAllBlogList(): void {
    this.blogService.getAllBlogList().pipe(take(1)).subscribe((res: any) => {
      if (!!res && res.length > 0) {
        this.blogList = res;
        console.log('>>>>', res)
      }
    });
  }

  openBlogDetail(blog: Blog): void {
    if (this.blogService.getUserRole() === 'user') {
      this.route.navigate(['/client/blog-detail/'+ blog?.postId]);
    } else {
      this.route.navigate(['/admin/blog-detail/'+ blog?.postId]);
    }

  }
}

<div *ngIf="blogList.length === 0" class="no-blogs-message">
  <p>There are no blogs available.</p>
</div>

<div *ngIf="blogList.length > 0" class="main-list">
  <div *ngFor="let blog of blogList" class="blog-item">
      <div class="card" (click)="openBlogDetail(blog)">
          <div class="card__header">
              <img [src]="blog?.postImage" alt="blog-image" class="card__image" width="100%">
          </div>
          <div class="card__body">
              <span class="tag tag-red">{{blog?.category}}</span>
              <h3 class="title">{{blog?.postTitle}}</h3>
          </div>
          <div class="card__footer">
              <div class="user">
                  <mat-icon>account_circle</mat-icon>
                  <div class="user__info">
                      <h5>{{blog?.user?.userName}}</h5>
                  </div>
              </div>
          </div>
      </div>
  </div>
</div>
