<div class="header">
    <!-- Logo/Header on the left -->
    <div class="logo">
        <p>Admin Dashboard</p>
    </div>

    <!-- Navigation Links on the left -->
    <div class="nav-links">
        <div (click)="routerToLink('/admin/home')" [class.selected]="url === '/admin/home'" class="nav-item">
            Home
        </div>
        <div (click)="routerToLink('/admin/create-blog')" [class.selected]="url === '/admin/create-blog'" class="nav-item">
            Create Blog
        </div>
        <div (click)="routerToLink('/admin/blog-list')" [class.selected]="url === '/admin/blog-list'" class="nav-item">
            Blog List
        </div>
    </div>

    <!-- User Menu Trigger (right side) -->
    <div [matMenuTriggerFor]="menu" class="user-name-item">
        <div>{{userName}}&nbsp;</div>
        <div>
            <mat-icon>expand_more</mat-icon>
        </div>
    </div>
</div>

<mat-menu #menu="matMenu">
    <button (click)="routerToLink('/admin/logout')" mat-menu-item>
        <mat-icon>logout</mat-icon> Logout
    </button>
</mat-menu>
